function fixEncoding(str) {
  if (!str) return "";
  try {
    return Buffer.from(str, "latin1").toString("utf8")
      .replace(/\s+/g, " ") // limpia saltos de l√≠nea extra
      .trim();
  } catch (e) {
    return str;
  }
}

const titles = items[0].json.title || [];
const links = items[0].json.link || [];

