// Suponiendo que los items llegan en items[0].json.results
const results = $input.first().json.result || []; 
const filtered = results.filter(item => item.nota === "No");

// Retornamos cada resultado como un item separado
return filtered.map(item => ({ json: item }));

// Separar los items con texto y los items con id_link
const itemsConTexto = items.filter(i => i.json.nota_text);
const itemsConId = items.filter(i => i.json.id_link);

// Combinar por "link"
const resultado = itemsConTexto.map(txtItem => {
  const match = itemsConId.find(idItem => idItem.json.link === txtItem.json.link);
  const notaText = Array.isArray(txtItem.json.nota_text)
    ? txtItem.json.nota_text.map(p => p.trim()).join("\n\n")
    : "";

  return {
    json: {
      nota_text: notaText,
      nota_link: txtItem.json.link,
      id_link: match ? match.json.id_link : null
    }
  };
});

return resultado;

